	(1, 'Titanmine', 2, 'Produziert Titan.', 'Produziert Titan.', 100, 45, 0, 0, 0, 10, 1.90, 0.20, 10, 0, 0, 65, 0, 0, 0, 0, 0, 1.50, 0, 0, 0, 0, 0, 0.00, 0, 50, 2, 1, 0, 0, 0, 0, 0, 0),
